import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import '../styles/login.css'

function Login() {
  //sets the username and password variables
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');


  //handler every time we enter info in the username field
  function handleUsername(event) {
    setUsername(event.target.value);
    console.log("changed uname");
  };

  //handler every time we enter info in the username field
  function handlePasswordChange (event) {
    setPassword(event.target.value);
    console.log("changed pass");
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log("Submitted!");
    console.log("username", username);
    console.log("password", password);
    try {
      const response = await axios.post('http://localhost:8090/login');
      console.log(response.data); // Log the response from the server
    } catch (error) {
      console.error('Error submitting form:', error);
    }


    /*
    try {
      const response = await axios.post('http://localhost:5000/login', {
        username,
        password
      });
      console.log('Login successful:', response.data);
      // Handle success, e.g., redirect to a dashboard or display a success message
    } catch (error) {
      console.error('Login failed:', error.response.data);
      // Handle error, e.g., display an error message to the user
    }
    */

  };

  return (
    <div>
      <Navbar></Navbar>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
            Username:
            <input
              type="text"
              value={username}
              onChange={handleUsernameChange}
            />
          </label>
          <br />
          <label>
            Password:
            <input
              type="password"
              value={password}
              onChange={handlePasswordChange}
            />
          </label>
          <br />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
