import React from 'react'
import Navbar from './Navbar';
import { useState } from 'react';
import '../styles/create_recipe.css'
function Create_Recipe() {
const [title, setNewTitle] = useState("")

function handleTitle(e){
  e.preventDefault()
}
  return (
    
    <div>
      <Navbar></Navbar>
      <div className='center container'>
        <h1 className='header'>Create a Custom Recipe</h1>
        <form className='form center'  onSubmit={handleTitle}>
          <label htmlFor='Title' className='headerCompliment'>Enter Recipe Title</label>
          <input value = {title} 
          onChange = {e => setNewTitle(e.target.value)} 
          type='text' 
          id="title" />
        </form>
        <form className='form center'  onSubmit={handleTitle}>
          <label htmlFor='Title' className='headerCompliment'>Enter Recipe Steps</label>
          <input value = {title} 
          onChange = {e => setNewTitle(e.target.value)} 
          type='textarea' 
          id="title" />
        </form>
        <h5 className='alignCenter'>This is your own convetion<br></br>
        Be as thorough or verbose as wanted<br></br>
        You can label the steps, write a paragraph,<br></br> 
        however you want to format your steps</h5>
      </div>
    </div>
  )
}

export default Create_Recipe;
